    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String fullName = jTextField1.getText();
        String userName = jTextField2.getText();
        String department = jComboBox1.getSelectedItem().toString();
        String password = jPasswordField1.getText();
        String confirmPassword = jPasswordField2.getText();
        Connection DBConn = null;
        String errString = null;
        Boolean connectError = false;

        if (fullName.trim().isEmpty()) {
            jLabel7.setText("Please enter your full name");
        } else if (userName.trim().isEmpty()) {
            jLabel7.setText("Please enter username");
        } else if (password.trim().isEmpty()) {
            jLabel7.setText("Please enter password");
        } else if (confirmPassword.trim().isEmpty()) {
            jLabel7.setText("Please confirm password");
        } else if (!password.equals(confirmPassword)) {
            jLabel7.setText("Passwords do not match, please check");
        } else {
            try {
                String sourceURL = "jdbc:mysql://localhost:3306/users";
                DBConn = DriverManager.getConnection(sourceURL, "root", "");
                String SQL = String.format("SELECT username FROM userdetails WHERE username = '%s'", userName);
                System.out.println(SQL);
                PreparedStatement stmt = DBConn.prepareStatement(SQL);
                ResultSet rs = stmt.executeQuery();

                if (!rs.next()) {
                    insertToUsers(fullName, userName, department, password, confirmPassword);
                } else {
                    jLabel7.setText("Username " + userName + " exists, please choose different username");
                }

            } catch (Exception e) {
                jLabel7.setText("Error connecting to userlog database");

                connectError = true;
            }
        }
    }//GEN-LAST:event_jButton1ActionPerformed

