    public void insertToUsers(String fullName, String userName, String department, String password, String confirmPassword) {
        Connection DBConn = null;
        int executeUpdateVal;
        try {
            String sourceURL = "jdbc:mysql://localhost:3306/users";
            DBConn = DriverManager.getConnection(sourceURL, "root", "");
                String SQL = String.format("INSERT INTO userdetails (full_name,username,password,department) VALUES ('%s', '%s','%s','%s')", fullName, userName, password, department);
            System.out.println(SQL);
            PreparedStatement stmt = DBConn.prepareStatement(SQL);
            executeUpdateVal = stmt.executeUpdate();
            System.out.println(executeUpdateVal);
            if (executeUpdateVal == 1) {
                jLabel7.setForeground(Color.black);
                jLabel7.setText("User " + userName + " has been created successfully, you can sign-in");
                jTextField1.setText("");
                jTextField2.setText("");
                jPasswordField1.setText("");
                jPasswordField2.setText("");
            }
            else{
                jLabel7.setText("Error creating user " + userName + " please try again later");
            }

        } catch (Exception e) {
            System.out.println(e);
            jLabel7.setText(" Error inserting value database");

        }
    }

